import 'package:flutter/material.dart';class AdminDashboardBox extends StatelessWidget {  const AdminDashboardBox({    super.key,    required this.size, required this.totalNumber, required this.text, required this.onClick, required this.bgColor  });  final Size size;  final dynamic totalNumber;  final String text;  final VoidCallback onClick;  final Color bgColor;  @override  Widget build(BuildContext context) {    return InkWell(        onTap: onClick,        child: Container(          width: size.width,          height: 120,          padding: EdgeInsets.all(10),          decoration: BoxDecoration(              color: bgColor,              borderRadius: BorderRadius.circular(10)          ),          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            crossAxisAlignment: CrossAxisAlignment.center,            children: [              Text("$totalNumber",                style: TextStyle(                    fontSize: 25,                    color: Colors.white,                    fontWeight: FontWeight.w600                ),              ),              SizedBox(height: 10,),              Text(text,                style: TextStyle(                    fontSize: 13,                    color: Colors.white,                    fontWeight: FontWeight.w400                ),              ),            ],          ),        ));  }}