import 'package:flutter/material.dart';class SingleUserListView extends StatelessWidget {  const SingleUserListView({    super.key,    required this.size, required this.image, required this.name, required this.email, required this.phone, required this.status, required this.onClick  });  final Size size;  final String image;  final String name;  final String email;  final String phone;  final bool status;  final VoidCallback onClick;  @override  Widget build(BuildContext context) {    return Container(      width: size.width,      margin: EdgeInsets.only(bottom: 10),      //height: 80,      padding: EdgeInsets.all(10),      decoration: BoxDecoration(        borderRadius: BorderRadius.circular(10),        color: Colors.white,      ),      child: ListTile(        leading: ClipRRect(            borderRadius: BorderRadius.circular(100),            child: Image.asset("assets/images/person.png", height: 50, width: 50, fit: BoxFit.cover,)),        title: Text(name,          style: TextStyle(            fontWeight: FontWeight.w500,            fontSize: 16,          ),        ),        subtitle: Column(          mainAxisAlignment: MainAxisAlignment.start,          crossAxisAlignment: CrossAxisAlignment.start,          children: [            SizedBox(height: 10,),            Text("$email",              style: TextStyle(                fontWeight: FontWeight.w400,                fontSize: 12,              ),            ),            SizedBox(height: 5,),            Text("$phone",              style: TextStyle(                fontWeight: FontWeight.w400,                fontSize: 12,              ),            ),            SizedBox(height: 5,),            Container(              padding: EdgeInsets.only(left: 20, right: 20, top: 5, bottom: 5),              decoration: BoxDecoration(                borderRadius: BorderRadius.circular(100),                color: status ? Colors.green : Colors.red,              ),              child: Text(status ? "Start Challenges" : "Not Start Challenges",                style: TextStyle(                  fontSize: 8,                  color: Colors.white                ),              ),            )          ],        ),        trailing: InkWell(          onTap: onClick,          child: Container(            width: 80,            height: 30,            decoration: BoxDecoration(                color: Colors.green,                borderRadius: BorderRadius.circular(5)            ),            child: Center(              child: Text("VIEW",                style: TextStyle(                    color: Colors.white,                    fontWeight: FontWeight.w400,                    fontSize: 10                ),              ),            ),          ),        ),      ),    );  }}